import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import "../components.css";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import HealingIcon from "@material-ui/icons/Healing";
import MonetizationOnIcon from "@material-ui/icons/MonetizationOn";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";

function Goal({ goal, index, completeGoal, removeGoal }) {
  return (
    <ListItem
      className="list-item"
      style={{ textDecoration: goal.isCompleted ? "line-through" : "", backgroundColor: goal.isCompleted ? "green" : ""  }}
    >
      <IconButton
        variant="outlined"
        color="secondary"
        onClick={() => removeGoal(index)}
        aria-label="delete"
      >
        <DeleteIcon fontSize="small" />
      </IconButton>
      <ListItemAvatar>
        <HealingIcon />
      </ListItemAvatar>
      {goal.text}
      <div>
        <Button variant="" color="green" onClick={() => completeGoal(index)}>
          Complete?
        </Button>
      </div>
    </ListItem>
  );
}

function GoalForm({ addGoal }) {
  const [value, setValue] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!value) return;
    addGoal(value);
    setValue("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        className="input"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
    </form>
  );
}

function Test() {
  const [goals, setGoals] = useState([
    {
      text:
        "I am a very very very very very very very very very very very very very very very very very very long default",
      isCompleted: false,
    },
    {
      text: "",
      isCompleted: false,
    },
    {
      text: "Default 3",
      isCompleted: false,
    },
  ]);

  const addGoal = (text) => {
    const newGoals = [...goals, { text }];
    setGoals(newGoals);
  };

  const completeGoal = (index) => {
    const newGoals = [...goals];
    newGoals[index].isCompleted = true;
    setGoals(newGoals);
  };

  const removeGoal = (index) => {
    const newGoals = [...goals];
    newGoals.splice(index, 1);
    setGoals(newGoals);
  };

  return (
    <div className="app">
      <div className="goal-list">
        <div>
          {" "}
          Add Goal
          <GoalForm addGoal={addGoal} />
        </div>
        {goals.map((goal, index) => (
          <Goal
            key={index}
            index={index}
            goal={goal}
            completeGoal={completeGoal}
            removeGoal={removeGoal}
          />
        ))}
      </div>
    </div>
  );
}

export default Test;
