import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import "../components.css";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Checkbox from "@material-ui/core/Checkbox";
import Avatar from "@material-ui/core/Avatar";
import HealingIcon from "@material-ui/icons/Healing";
import MonetizationOnIcon from "@material-ui/icons/MonetizationOn";

function Goal({ goals, index, completeGoal, removeGoal }) {
  return (
    <ListItem goals={goals} button>
      <ListItemAvatar>
        <HealingIcon />
      </ListItemAvatar>
      <ListItemText primary={goals.text} />
      <ListItemSecondaryAction>
        <Checkbox
          edge="end"
          onChange={() => completeGoal(index)}
          checked={checked.indexOf() !== -1}
        />
      </ListItemSecondaryAction>
    </ListItem>

    // <div
    //   className="goal"
    //   style={{ textDecoration: goal.isCompleted ? "line-through" : "" }}
    // >
    //   {goal.text}

    //   <div>
    //     <button onClick={() => completeGoal(index)}>Complete</button>
    //     <button onClick={() => removeGoal(index)}>x</button>
    //   </div>
    // </div>
  );
}

function GoalForm({ addGoal }) {
  const [value, setValue] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!value) return;
    addGoal(value);
    setValue("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        className="input"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
    </form>
  );
}

function Test() {
  const [goals, setGoals] = useState([
    {
      text: "Learn about React",
      isCompleted: false,
    },
    {
      text: "Meet friend for lunch",
      isCompleted: false,
    },
    {
      text: "Build really cool goal app",
      isCompleted: false,
    },
  ]);

  const addGoal = (text) => {
    const newGoals = [...goals, { text }];
    setGoals(newGoals);
  };

  const completeGoal = (index) => {
    const newGoals = [...goals];
    newGoals[index].isCompleted = true;
    setGoals(newGoals);
  };

  const removeGoal = (index) => {
    const newGoals = [...goals];
    newGoals.splice(index, 1);
    setGoals(newGoals);
  };

  return (
    <div className="app">
      <div className="goal-list">
        {goals.map((goal, index) => (
          <Goal
            key={index}
            index={index}
            goal={goal}
            completeGoal={completeGoal}
            removeGoal={removeGoal}
          />
        ))}
        <GoalForm addGoal={addGoal} />
      </div>
    </div>
  );
}

export default Test;
