import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import "../components.css";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Checkbox from "@material-ui/core/Checkbox";
import Avatar from "@material-ui/core/Avatar";
import HealingIcon from "@material-ui/icons/Healing";
import MonetizationOnIcon from "@material-ui/icons/MonetizationOn";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    backgroundColor: theme.palette.background.paper,
  },
}));

export default function GoalsTest() {

    const classes = useStyles();
  const [checked, setChecked] = React.useState([1]);

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  const [goals, setGoals] = useState([
    { text: "Goal 1" },
    { text: "Goal 2" },
    { text: "Goal 3" },
  ]);

  const Goal = ({ goals }) => (
    <ListItem goals={goals} button>
      <ListItemAvatar>
        <HealingIcon />
      </ListItemAvatar>
      <ListItemText primary={goals.text} />
      <ListItemSecondaryAction>
        <Checkbox
          edge="end"
          onChange={handleToggle()}
          checked={checked.indexOf() !== -1}
        />
      </ListItemSecondaryAction>
    </ListItem>
  );

  return (
    <List dense className={classes.root}>
      {/* default list items */}
      <ListItem button>
        <ListItemAvatar>
          <MonetizationOnIcon />
        </ListItemAvatar>
        <ListItemText primary={`Default 1`} />
        <ListItemSecondaryAction>
          <Checkbox edge="end" />
        </ListItemSecondaryAction>
      </ListItem>
      <ListItem button>
        <ListItemAvatar>
          <HealingIcon />
        </ListItemAvatar>
        <ListItemText primary={`Default 2`} />
        <ListItemSecondaryAction>
          <Checkbox edge="end" />
        </ListItemSecondaryAction>
      </ListItem>

      {goals.map((goal, index) => {
        return <Goal key="index"></Goal>;
      })}
    </List>
  );
}
